mapper.select:
SELECT * FROM AedEquipment e
LEFT JOIN AedPosition p ON e.equipmentId = p.equipmentId
WHERE e.equipmentId = #{equipmentId};

mapper.insert:
INSERT INTO
AedEquipment(displayTime, productionTime, purchaseTime, factoryName, model, status)
VALUES (#{displayTime}, #{productionTime}, #{purchaseTime}, #{factoryName}, #{model}, #{status});
